import React, { useState, useEffect } from 'react';

const Events: React.FC = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const searchEvents = async () => {
    setLoading(true);
    setError('');

    try {
      // For demo purposes, using a mock location (San Francisco)
      const response = await fetch('http://localhost:5001/api/events?lat=37.7749&lng=-122.4194&radius=10');

      if (!response.ok) {
        throw new Error('Failed to fetch events');
      }

      const data = await response.json();
      setEvents(data.events || []);
    } catch (err) {
      setError('Failed to load events. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    searchEvents();
  }, []);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div>
      <h1 className="text-3xl font-bold mb-6">Events</h1>

      {loading && (
        <div className="text-center py-8">
          <p>Loading events...</p>
        </div>
      )}

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {!loading && !error && events.length === 0 && (
        <div className="text-center py-8">
          <p>No events found. Please try a different location or date range.</p>
        </div>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {events.map((event: any) => (
          <div key={event.id} className="bg-white rounded-lg shadow-md overflow-hidden">
            {event.logo && (
              <img 
                src={event.logo} 
                alt={event.name}
                className="w-full h-48 object-cover"
              />
            )}
            <div className="p-4">
              <h3 className="text-xl font-semibold mb-2">{event.name}</h3>
              {event.startDate && (
                <p className="text-gray-600 mb-2">
                  {formatDate(event.startDate.local)}
                </p>
              )}
              {event.venue && (
                <p className="text-gray-600 mb-2">{event.venue.name}</p>
              )}
              {event.isFree && (
                <span className="bg-green-100 text-green-800 px-2 py-1 rounded text-sm">
                  Free
                </span>
              )}
              {event.url && (
                <a 
                  href={event.url} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="block mt-3 text-blue-600 hover:text-blue-800"
                >
                  View Event â†’
                </a>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
