import React, { useState, useEffect } from 'react';

const Restaurants: React.FC = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const searchRestaurants = async () => {
    setLoading(true);
    setError('');

    try {
      // For demo purposes, using a mock location (San Francisco)
      const response = await fetch('http://localhost:5001/api/restaurants?lat=37.7749&lng=-122.4194&radius=1500');

      if (!response.ok) {
        throw new Error('Failed to fetch restaurants');
      }

      const data = await response.json();
      setRestaurants(data.restaurants || []);
    } catch (err) {
      setError('Failed to load restaurants. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    searchRestaurants();
  }, []);

  return (
    <div>
      <h1 className="text-3xl font-bold mb-6">Restaurants</h1>

      {loading && (
        <div className="text-center py-8">
          <p>Loading restaurants...</p>
        </div>
      )}

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {!loading && !error && restaurants.length === 0 && (
        <div className="text-center py-8">
          <p>No restaurants found. Please try a different location.</p>
        </div>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {restaurants.map((restaurant: any) => (
          <div key={restaurant.id} className="bg-white rounded-lg shadow-md overflow-hidden">
            {restaurant.photos && restaurant.photos.length > 0 && (
              <img 
                src={restaurant.photos[0]} 
                alt={restaurant.name}
                className="w-full h-48 object-cover"
              />
            )}
            <div className="p-4">
              <h3 className="text-xl font-semibold mb-2">{restaurant.name}</h3>
              <p className="text-gray-600 mb-2">{restaurant.address}</p>
              {restaurant.rating && (
                <div className="flex items-center mb-2">
                  <span className="text-yellow-500">â˜…</span>
                  <span className="ml-1">{restaurant.rating}</span>
                  {restaurant.totalRatings && (
                    <span className="text-gray-500 ml-1">({restaurant.totalRatings} reviews)</span>
                  )}
                </div>
              )}
              {restaurant.priceLevel && (
                <p className="text-gray-600">Price: {'$'.repeat(restaurant.priceLevel)}</p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Restaurants;
